import React, { useState } from 'react';
import '../styles/RosterView.css';

function RosterView({ sheet }) {
  // Display only the first 8 columns.
  const displayColCount = 8;
  const [visibleComment, setVisibleComment] = useState(null);

  // Group rows into shift blocks by detecting shift names.
  const shiftLabels = ["Owl Shift", "Day Shift", "Swing Shift"];
  const groupByShift = (rows) => {
    console.log('Grouping by shift, total rows:', rows.length);

    const blocks = [];
    let currentBlock = [];

    // First, try to find shift labels in any cell of each row
    for (let i = 0; i < rows.length; i++) {
      const row = rows[i];
      let foundShiftLabel = false;

      // Check each cell in the row for shift labels
      for (let j = 0; j < row.length; j++) {
        const cell = row[j];
        const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
        const cellStr = cellValue !== null && cellValue !== undefined ? String(cellValue) : '';

        if (shiftLabels.some(label => cellStr.includes(label))) {
          console.log('Found shift label at row', i, 'cell', j, ':', cellStr);
          foundShiftLabel = true;

          if (currentBlock.length > 0) {
            blocks.push(currentBlock);
          }

          currentBlock = [row];
          break;
        }
      }

      if (!foundShiftLabel && currentBlock.length > 0) {
        currentBlock.push(row);
      }
    }

    if (currentBlock.length > 0) blocks.push(currentBlock);

    // If no shift blocks were found, try a more lenient approach
    if (blocks.length === 0 && rows.length > 0) {
      console.log('No shift blocks found with strict matching, using fallback...');

      // Look for any row that might contain time information (e.g., "11:00 - 11:30")
      let timeBlockStarted = false;

      for (let i = 0; i < rows.length; i++) {
        const row = rows[i];
        let containsTimeInfo = false;

        for (let j = 0; j < row.length; j++) {
          const cell = row[j];
          const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
          const cellStr = cellValue !== null && cellValue !== undefined ? String(cellValue) : '';

          // Check for time patterns like "XX:XX - XX:XX"
          if (cellStr.match(/\d{1,2}:\d{2}\s*-\s*\d{1,2}:\d{2}/)) {
            containsTimeInfo = true;
            break;
          }
        }

        if (containsTimeInfo) {
          if (!timeBlockStarted) {
            // Start a new block with a header row
            currentBlock = [
              // Create a synthetic header row
              ["Shift Block", "", "", "", "", "", "", ""]
            ];
            timeBlockStarted = true;
          }
          currentBlock.push(row);
        } else if (timeBlockStarted && row.some(cell => cell)) {
          // Add non-empty rows to the current time block
          currentBlock.push(row);
        } else if (timeBlockStarted) {
          // Empty row after time block - end the current block
          if (currentBlock.length > 0) {
            blocks.push(currentBlock);
          }
          timeBlockStarted = false;
          currentBlock = [];
        }
      }

      // Add the last block if it exists
      if (currentBlock.length > 0) {
        blocks.push(currentBlock);
      }

      // If still no blocks, just put all rows in one block
      if (blocks.length === 0 && rows.length > 0) {
        blocks.push([
          ["All Shifts", "", "", "", "", "", "", ""],
          ...rows
        ]);
      }
    }

    console.log('Found', blocks.length, 'shift blocks');
    return blocks;
  };

  if (!sheet) {
    console.error('No sheet data provided to RosterView');
    return <div>No data available for this week</div>;
  }

  console.log('RosterView received sheet:', sheet);

  // We're now directly using the sheet.data from the API
  const allRows = sheet.data || [];
  console.log('Total rows in sheet:', allRows.length);

  // Log the first few rows for debugging
  if (allRows.length > 0) {
    console.log('First 3 rows:', allRows.slice(0, 3));
  }

  // Find the title row (usually contains "Operations Shift Schedule")
  let titleRow = null;
  for (let i = 0; i < Math.min(10, allRows.length); i++) {
    const row = allRows[i];
    for (let j = 0; j < row.length; j++) {
      const cell = row[j];
      const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
      if (cellValue && String(cellValue).includes('Operations Shift Schedule')) {
        titleRow = row;
        console.log('Found title row at index', i);
        break;
      }
    }
    if (titleRow) break;
  }

  // Set a title based on the title row or use a default
  const weekHeader = titleRow ? 
    (titleRow[0] && (typeof titleRow[0] === 'object' ? titleRow[0].value : titleRow[0])) : 
    (sheet.weekHeader || 'Weekly Schedule');

  // Skip the title row if found, otherwise use all rows
  const startIndex = titleRow ? allRows.indexOf(titleRow) + 1 : 0;
  const rowsToUse = allRows.slice(startIndex);
  console.log('Rows to use for shift blocks:', rowsToUse.length, 'starting from index', startIndex);

  const shiftBlocks = groupByShift(rowsToUse);

  const handleCellClick = (blockIndex, rowIndex, cellIndex, comment) => {
    setVisibleComment({
      blockIndex,
      rowIndex,
      cellIndex,
      comment
    });
  };

  return (
    <div className="roster-view">
      <div className="week-header">
        <h2>{weekHeader}</h2>
      </div>
      {shiftBlocks.length > 0 ? (
        // We have shift blocks to display
        shiftBlocks.map((block, blockIndex) => {
          const shiftHeader = block[0] && 
            (typeof block[0][0] === 'object' ? block[0][0].value : block[0][0]) || 'Shift';
          return (
            <div key={blockIndex} className="shift-block">
              <h3 className="shift-header">{shiftHeader}</h3>
              <div className="shift-content">
                {block.slice(1).map((row, rowIndex) => (
                  <div key={rowIndex} className="shift-row">
                    {row.slice(0, displayColCount).map((cell, cellIndex) => {
                      let cellValue = '';
                      let comment = '';
                      if (cell && typeof cell === 'object' && 'value' in cell) {
                        cellValue = cell.value !== undefined ? String(cell.value) : '';
                        comment = cell.comment || '';
                      } else {
                        cellValue = cell !== undefined ? String(cell) : '';
                      }
                      return (
                        <div 
                          key={cellIndex} 
                          className="cell" 
                          onClick={() => comment && handleCellClick(blockIndex, rowIndex, cellIndex, comment)}
                        >
                          <span className="cell-content">{cellValue}</span>
                          {comment && <span className="comment-indicator" title="Has comment">â€¢</span>}
                          {visibleComment &&
                           visibleComment.blockIndex === blockIndex &&
                           visibleComment.rowIndex === rowIndex &&
                           visibleComment.cellIndex === cellIndex && (
                            <div className="comment-tooltip">
                              {visibleComment.comment}
                            </div>
                          )}
                        </div>
                      );
                    })}
                  </div>
                ))}
              </div>
              {blockIndex < shiftBlocks.length - 1 && <hr className="block-divider" />}
            </div>
          );
        })
      ) : (
        <div>
          <div>No shift data available for this week</div>
          <div className="debug-info">
            <p>Debug Info:</p>
            <p>Week Header: {weekHeader}</p>
            <p>Available Rows: {rowsToUse.length}</p>
            <p>Shift Blocks Found: {shiftBlocks.length}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default RosterView;
