import React, { useState, useEffect } from 'react';
import RosterView from './components/RosterView';
import Pagination from './components/Pagination';
import './styles/App.css';

function App() {
  const [rosterWeeks, setRosterWeeks] = useState([]);
  const [currentWeekIndex, setCurrentWeekIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Group rows from the Excel data into week blocks.
  // Here we assume each week block begins with a row that contains "Operations Shift Schedule".
  function groupRowsByWeek(rows) {
    const weeks = [];
    let currentWeek = null;
    rows.forEach(row => {
      const firstCell = row[0] && (typeof row[0] === 'object' ? row[0].value : row[0]) || '';
      if (firstCell && firstCell.includes("Operations Shift Schedule")) {
        if (currentWeek) weeks.push(currentWeek);
        currentWeek = {
          weekHeader: firstCell,
          rows: [row]
        };
      } else if (currentWeek) {
        currentWeek.rows.push(row);
      }
    });
    if (currentWeek) weeks.push(currentWeek);
    return weeks;
  }

  useEffect(() => {
    const fetchSchedule = async () => {
      try {
        setLoading(true);
        setError(null);
        // Adjust the URL if your Django server is on a different host/port.
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 second timeout

        try {
          const response = await fetch("http://localhost:8000/api/schedule/", {
            signal: controller.signal
          });
          clearTimeout(timeoutId);

          if (!response.ok) {
            throw new Error(`Server error: ${response.status} ${response.statusText}`);
          }

          const data = await response.json();
          if (data.sheets && data.sheets.length > 0) {
            const sheet = data.sheets[0];  // Use first sheet for this example
            if (sheet.data && sheet.data.length > 0) {
              const weeks = groupRowsByWeek(sheet.data);
              setRosterWeeks(weeks);
              setCurrentWeekIndex(0);
            } else {
              setError("No data found in the schedule sheet");
            }
          } else {
            setError("No sheets found in the schedule data");
          }
        } catch (fetchErr) {
          if (fetchErr.name === 'AbortError') {
            throw new Error('Request timed out. Please check your connection and try again.');
          }
          throw fetchErr;
        }
      } catch (err) {
        console.error('Error fetching schedule:', err);
        setError(err.message || 'An unexpected error occurred');
      } finally {
        setLoading(false);
      }
    };

    fetchSchedule();
  }, []);

  const goToNext = () => {
    if (currentWeekIndex < rosterWeeks.length - 1) {
      setCurrentWeekIndex(currentWeekIndex + 1);
    }
  };

  const goToPrevious = () => {
    if (currentWeekIndex > 0) {
      setCurrentWeekIndex(currentWeekIndex - 1);
    }
  };

  return (
    <div className="container">
      <h1>Weekly Operations Shift Schedule</h1>
      {loading && <div className="loading">Loading schedule data...</div>}
      {error && <div className="error">Error: {error}</div>}
      {(!loading && !error && rosterWeeks.length > 0) ? (
        <>
          <RosterView sheet={rosterWeeks[currentWeekIndex]} />
          <Pagination 
            currentIndex={currentWeekIndex} 
            total={rosterWeeks.length} 
            onPrevious={goToPrevious} 
            onNext={goToNext}
          />
        </>
      ) : !loading && !error && <p>No schedule data found.</p>}
    </div>
  );
}

export default App;
