import React, { useState } from 'react';
import '../styles/RosterView.css';

function RosterView({ sheet }) {
  // Display only the first 8 columns.
  const displayColCount = 8;
  const [visibleComment, setVisibleComment] = useState(null);

  // For this specific Excel format, we'll create a custom grouping function
  const groupByShift = (rows) => {
    console.log('Grouping data, total rows:', rows.length);

    if (rows.length === 0) {
      return [];
    }

    // Based on the API data structure, we know the schedule has a specific format
    // First, find the row with "Meal Periods" which is the start of the schedule data
    let mealPeriodRowIndex = -1;
    for (let i = 0; i < rows.length; i++) {
      const row = rows[i];
      for (let j = 0; j < row.length; j++) {
        const cell = row[j];
        const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
        const cellStr = cellValue !== null && cellValue !== undefined ? String(cellValue) : '';

        if (cellStr === 'Meal Periods') {
          console.log('Found Meal Periods row at index', i);
          mealPeriodRowIndex = i;
          break;
        }
      }
      if (mealPeriodRowIndex !== -1) break;
    }

    // If we didn't find the meal periods row, try to create a meaningful display anyway
    if (mealPeriodRowIndex === -1) {
      console.log('Could not find Meal Periods row, using all data');
      // Just return all non-empty rows as one block
      const nonEmptyRows = rows.filter(row => row.some(cell => {
        const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
        return cellValue !== null && cellValue !== undefined && cellValue !== '';
      }));

      if (nonEmptyRows.length > 0) {
        return [nonEmptyRows];
      }
      return [];
    }

    // We found the meal periods row, now we can extract the schedule data
    // The first block will be the header rows (days of week, dates)
    const headerRows = rows.slice(Math.max(0, mealPeriodRowIndex - 2), mealPeriodRowIndex + 1);

    // Now look for shift blocks after the meal periods row
    const blocks = [];
    let currentBlock = [];
    let inBlock = false;

    for (let i = mealPeriodRowIndex; i < rows.length; i++) {
      const row = rows[i];

      // Check if this row has content in the first few columns
      const hasContent = row.slice(0, 3).some(cell => {
        const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
        return cellValue !== null && cellValue !== undefined && cellValue !== '';
      });

      if (hasContent) {
        // This could be a shift header or part of a shift block
        if (!inBlock) {
          // Start a new block
          currentBlock = [row];
          inBlock = true;
        } else {
          // Add to current block
          currentBlock.push(row);
        }
      } else if (inBlock) {
        // Empty row after a block - end the current block
        if (currentBlock.length > 0) {
          blocks.push([...headerRows, ...currentBlock]);
        }
        inBlock = false;
        currentBlock = [];
      }
    }

    // Add the last block if it exists
    if (inBlock && currentBlock.length > 0) {
      blocks.push([...headerRows, ...currentBlock]);
    }

    // If we didn't find any blocks, create one with all the data
    if (blocks.length === 0) {
      blocks.push([...headerRows, ...rows.slice(mealPeriodRowIndex + 1)]);
    }

    console.log('Found', blocks.length, 'data blocks');
    return blocks;
  };

  if (!sheet) {
    console.error('No sheet data provided to RosterView');
    return <div>No data available for this week</div>;
  }

  console.log('RosterView received sheet:', sheet);

  // We're now directly using the sheet.data from the API
  const allRows = sheet.data || [];
  console.log('Total rows in sheet:', allRows.length);

  // Find the title row (usually contains "Operations Shift Schedule")
  let titleRow = null;
  let weekHeader = 'Weekly Operations Shift Schedule';

  for (let i = 0; i < Math.min(10, allRows.length); i++) {
    const row = allRows[i];
    for (let j = 0; j < row.length; j++) {
      const cell = row[j];
      const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
      if (cellValue && String(cellValue).includes('Operations Shift Schedule')) {
        titleRow = row;
        weekHeader = String(cellValue);
        console.log('Found title row at index', i, ':', weekHeader);
        break;
      }
    }
    if (titleRow) break;
  }

  // Find date range if available
  let dateRange = '';
  if (titleRow && allRows.length > allRows.indexOf(titleRow) + 1) {
    const nextRow = allRows[allRows.indexOf(titleRow) + 1];
    for (let j = 0; j < nextRow.length; j++) {
      const cell = nextRow[j];
      const cellValue = cell && (typeof cell === 'object' ? cell.value : cell);
      if (cellValue && String(cellValue).includes('through')) {
        const startDate = nextRow[j-1] && (typeof nextRow[j-1] === 'object' ? nextRow[j-1].value : nextRow[j-1]);
        const endDate = nextRow[j+1] && (typeof nextRow[j+1] === 'object' ? nextRow[j+1].value : nextRow[j+1]);
        if (startDate && endDate) {
          dateRange = `${startDate} through ${endDate}`;
          console.log('Found date range:', dateRange);
        }
        break;
      }
    }
  }

  const shiftBlocks = groupByShift(rowsToUse);

  const handleCellClick = (blockIndex, rowIndex, cellIndex, comment) => {
    setVisibleComment({
      blockIndex,
      rowIndex,
      cellIndex,
      comment
    });
  };

  return (
    <div className="roster-view">
      <div className="week-header">
        <h2>{weekHeader}</h2>
      </div>
      {shiftBlocks.length > 0 ? (
        // We have shift blocks to display
        shiftBlocks.map((block, blockIndex) => {
          const shiftHeader = block[0] && 
            (typeof block[0][0] === 'object' ? block[0][0].value : block[0][0]) || 'Shift';
          return (
            <div key={blockIndex} className="shift-block">
              <h3 className="shift-header">{shiftHeader}</h3>
              <div className="shift-content">
                {block.slice(1).map((row, rowIndex) => (
                  <div key={rowIndex} className="shift-row">
                    {row.slice(0, displayColCount).map((cell, cellIndex) => {
                      let cellValue = '';
                      let comment = '';
                      if (cell && typeof cell === 'object' && 'value' in cell) {
                        cellValue = cell.value !== undefined ? String(cell.value) : '';
                        comment = cell.comment || '';
                      } else {
                        cellValue = cell !== undefined ? String(cell) : '';
                      }
                      return (
                        <div 
                          key={cellIndex} 
                          className="cell" 
                          onClick={() => comment && handleCellClick(blockIndex, rowIndex, cellIndex, comment)}
                        >
                          <span className="cell-content">{cellValue}</span>
                          {comment && <span className="comment-indicator" title="Has comment">â€¢</span>}
                          {visibleComment &&
                           visibleComment.blockIndex === blockIndex &&
                           visibleComment.rowIndex === rowIndex &&
                           visibleComment.cellIndex === cellIndex && (
                            <div className="comment-tooltip">
                              {visibleComment.comment}
                            </div>
                          )}
                        </div>
                      );
                    })}
                  </div>
                ))}
              </div>
              {blockIndex < shiftBlocks.length - 1 && <hr className="block-divider" />}
            </div>
          );
        })
      ) : (
        <div>
          <div>No shift data available for this week</div>
          <div className="debug-info">
            <p>Debug Info:</p>
            <p>Week Header: {weekHeader}</p>
            <p>Available Rows: {rowsToUse.length}</p>
            <p>Shift Blocks Found: {shiftBlocks.length}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default RosterView;
